import { ConditionalInput } from '@useparagon/core/steps/library/conditional';

/**
 * Search Tasks intent type
 */
type ClickupSearchTasks = {
  /**
   * Filter search
   * Search for tasks that match specified filters.
   *
   */
  taskFilterFormula?: ConditionalInput;
};

/**
 * Get Tasks in List intent type
 */
type ClickupGetTaskInList = {
  /**
   * List
   * Select a List to get tasks from. Use Connect Portal User Settings to allow users to select a ClickUp List.
   *
   */
  listId: string;

  /**
   * Filter search
   * Search for tasks that match specified filters.
   *
   */
  taskFilterFormula?: ConditionalInput;
};

/**
 * Create Task intent type
 */
type ClickupCreateTask = {
  /**
   * List
   * Select a List to create this task in. Use Connect Portal User Settings to allow users to select a ClickUp List.
   *
   */
  listId: string;

  /**
   * Name
   *
   */
  name: string;

  /**
   * Description
   *
   */
  description?: string;

  /**
   * Status
   * Select a Status for this task. Use Connect Portal User Settings to allow users to select a ClickUp Status.
   *
   */
  status?: string;

  /**
   * Assignees
   * Select a Member (or an array of member IDs) to be assigned to this task. Use Connect Portal User Settings to allow users to select a ClickUp Member.
   *
   */
  assignees?: string;

  /**
   * Due Date
   * Specify a date for this task to be due on.
   *
   */
  dueDate?: string;

  /**
   * Additional Fields
   * Specify additional fields to include on this task as JSON.
   *
   */
  additionalFields?: string;
};

/**
 * Update Task intent type
 */
type ClickupUpdateTask = {
  /**
   * Task ID
   * The ID of the task to update.
   *
   */
  taskId: string;

  /**
   * List
   * Select a List to create this task in. Use Connect Portal User Settings to allow users to select a ClickUp List.
   *
   */
  listId: string;

  /**
   * Name
   *
   */
  name?: string;

  /**
   * Description
   *
   */
  description?: string;

  /**
   * Status
   * Select a Status for this task. Use Connect Portal User Settings to allow users to select a ClickUp Status.
   *
   */
  status?: string;

  /**
   * Assignees
   * Select a Member (or an array of member IDs) to be assigned to this task. Use Connect Portal User Settings to allow users to select a ClickUp Member.
   *
   */
  assignees?: string;

  /**
   * Due Date
   * Specify a date for this task to be due on.
   *
   */
  dueDate?: string;

  /**
   * Additional Fields
   * Specify additional fields to include on this task as JSON.
   *
   */
  additionalFields?: string;
};

/**
 * Delete Task intent type
 */
type ClickupDeleteTask = {
  /**
   * Task ID
   * The ID of the task to delete.
   *
   */
  taskId: string;
};

export type IntentToInputOutput = {
  /**
   * CLICKUP_SEARCH_TASKS
   */
  CLICKUP_SEARCH_TASKS: [ClickupSearchTasks, any];

  /**
   * CLICKUP_GET_TASK_IN_LIST
   */
  CLICKUP_GET_TASK_IN_LIST: [ClickupGetTaskInList, any];

  /**
   * CLICKUP_CREATE_TASK
   */
  CLICKUP_CREATE_TASK: [ClickupCreateTask, any];

  /**
   * CLICKUP_UPDATE_TASK
   */
  CLICKUP_UPDATE_TASK: [ClickupUpdateTask, any];

  /**
   * CLICKUP_DELETE_TASK
   */
  CLICKUP_DELETE_TASK: [ClickupDeleteTask, any];
};
