/**
 * New Folder Created intent type
 */
type ClickupTriggerFolderCreated = {};

/**
 * Folder Updated intent type
 */
type ClickupTriggerFolderUpdated = {
  /**
   * Space ID
   * Trigger when a Folder is created within the provided Space. Use Connect Portal Workflow Settings to allow users to select a ClickUp Space. Defaults to watching all Spaces.
   *
   */
  spaceId?: string;
};

/**
 * New List Created intent type
 */
type ClickupTriggerListCreated = {
  /**
   * Space ID
   * Trigger when a List is created within this Space. Use Connect Portal Workflow Settings to allow users to select a ClickUp Space. Defaults to all Spaces.
   *
   */
  spaceId?: string;

  /**
   * Folder ID
   * Trigger when a List is created within this Folder. Use Connect Portal Workflow Settings to allow users to select a ClickUp Space. Defaults to provided Space.
   *
   */
  folderId?: string;
};

/**
 * List Updated intent type
 */
type ClickupTriggerListUpdated = {
  /**
   * Space ID
   * Trigger when a List is updated within this Space. Use Connect Portal Workflow Settings to allow users to select a ClickUp Space. Defaults to all Spaces.
   *
   */
  spaceId?: string;

  /**
   * Folder ID
   * Trigger when a List is updated within this Folder. Use Connect Portal Workflow Settings to allow users to select a ClickUp Folder. Defaults to provided Folder.
   *
   */
  folderId?: string;

  /**
   * List ID
   * Provide a List to watch for updates. Use Connect Portal Workflow Settings to allow users to select a ClickUp List. Defaults to provided Folder.
   *
   */
  listId?: string;
};

/**
 * New Space Created intent type
 */
type ClickupTriggerSpaceCreated = {};

/**
 * Space Updated intent type
 */
type ClickupTriggerSpaceUpdated = {
  /**
   * Space ID
   * Provide a Space to watch for updates. Use Connect Portal Workflow Settings to allow users to select a ClickUp Space. Defaults to all Spaces.
   *
   */
  spaceId?: string;
};

/**
 * New Task Created intent type
 */
type ClickupTriggerTaskCreated = {
  /**
   * List
   * Select a List to create this task in. Use Connect Portal User Settings to allow users to select a ClickUp List.
   *
   */
  listId: string;
};

/**
 * Task Updated intent type
 */
type ClickupTriggerTaskUpdated = {
  /**
   * List
   * Select a List to create this task in. Use Connect Portal User Settings to allow users to select a ClickUp List.
   *
   */
  listId: string;
};

/**
 * New Comment on Task intent type
 */
type ClickupTriggerTaskNewCommentAdded = {
  /**
   * List ID
   * Trigger when a comment is added to a Task within the provided List. Use Connect Portal Workflow Settings to allow users to select a ClickUp List.
   *
   */
  listId: string;

  /**
   * Task ID
   * Trigger when a comment is added to the provided Task. Defaults to subscribing to all Tasks within the List. Use Connect Portal Workflow Settings to allow users to select a ClickUp Task.
   *
   */
  taskId?: string;
};

export type IntentToInputOutput = {
  /**
   * CLICKUP_TRIGGER_FOLDER_CREATED
   */
  CLICKUP_TRIGGER_FOLDER_CREATED: [ClickupTriggerFolderCreated, any];

  /**
   * CLICKUP_TRIGGER_FOLDER_UPDATED
   */
  CLICKUP_TRIGGER_FOLDER_UPDATED: [ClickupTriggerFolderUpdated, any];

  /**
   * CLICKUP_TRIGGER_LIST_CREATED
   */
  CLICKUP_TRIGGER_LIST_CREATED: [ClickupTriggerListCreated, any];

  /**
   * CLICKUP_TRIGGER_LIST_UPDATED
   */
  CLICKUP_TRIGGER_LIST_UPDATED: [ClickupTriggerListUpdated, any];

  /**
   * CLICKUP_TRIGGER_SPACE_CREATED
   */
  CLICKUP_TRIGGER_SPACE_CREATED: [ClickupTriggerSpaceCreated, any];

  /**
   * CLICKUP_TRIGGER_SPACE_UPDATED
   */
  CLICKUP_TRIGGER_SPACE_UPDATED: [ClickupTriggerSpaceUpdated, any];

  /**
   * CLICKUP_TRIGGER_TASK_CREATED
   */
  CLICKUP_TRIGGER_TASK_CREATED: [ClickupTriggerTaskCreated, any];

  /**
   * CLICKUP_TRIGGER_TASK_UPDATED
   */
  CLICKUP_TRIGGER_TASK_UPDATED: [ClickupTriggerTaskUpdated, any];

  /**
   * CLICKUP_TRIGGER_TASK_NEW_COMMENT_ADDED
   */
  CLICKUP_TRIGGER_TASK_NEW_COMMENT_ADDED: [
    ClickupTriggerTaskNewCommentAdded,
    any,
  ];
};
