/**
 * Create Comment intent type
 */
type FigmaCreateComment = {
  /**
   * File ID
   * Specify a File ID. The File ID can be parsed from file url: https://www.figma.com/file/:id/:title
   *
   * **Example**:
   * ```
   * CRcEzuiEc3ybXCg6B
   * ```
   */
  fileId: string;

  /**
   * Message
   * The text contents of the comment to post
   *
   */
  message: string;

  /**
   * Additional Fields
   *
   * **Example**:
   * ```
   * {
   *  "comment_id": "string",
   *  "client_meta": {
   *    "x":100,
   *    "y":100
   *  }
   *}
   * ```
   */
  additionalData?: string;
};

