/**
 * Create Comment Reaction intent type
 */
type FigmaCreateCommentReaction = {
  /**
   * File ID
   * Specify a File ID. The File ID can be parsed from file url: https://www.figma.com/file/:id/:title
   *
   * **Example**:
   * ```
   * CRcEzuiEc3ybXCg6B
   * ```
   */
  fileId: string;

  /**
   * Comment ID
   * Specify the ID of the comment to add reaction to.
   *
   * **Example**:
   * ```
   * 469424202
   * ```
   */
  commentId: string;

  /**
   * Emoji
   * Specify the emoji type of reaction to add.
   *
   */
  emoji:
    | ':eyes:'
    | ':heart_eyes:'
    | ':heavy_plus_sign:'
    | ':+1:'
    | ':-1:'
    | ':joy:'
    | ':fire:';
};

