/**
 * Issue Created intent type
 */
type GithubTriggerIssueCreated = {} & (
  | {
      /**
       * Trigger Type
       *
       */
      apiType: 'organization';

      /**
       * Organization
       * Specify the Organization name. The name is not case sensitive.
       *
       * **Example**:
       * ```
       * Paragon
       * ```
       */
      organization: string;
    }
  | {
      /**
       * Trigger Type
       *
       */
      apiType: 'repository';

      /**
       * Owner
       * Specify the name of the account owner of the associated repository for this Issue.
       *
       * **Example**:
       * ```
       * abc
       * ```
       */
      owner: string;

      /**
       * Repository
       * Specify the name of the associated repository for this Issue.
       *
       */
      repo: string;
    }
  | {
      /**
       * Trigger Type
       *
       */
      apiType: 'githubApp';
    }
);

