/**
 * Add a new Call intent type
 */
type GongAddCall = {
  /**
   * Call Start Date
   * Accepts ISO 8601 date format.
   *
   * **Example**:
   * ```
   * 2022-08-01T07:00:00.000Z
   * ```
   */
  actualStart: string;

  /**
   * Client Unique ID
   * A call's unique identifier in the PBX or the recording system. Gong uses this identifier to prevent repeated attempts to upload the same recording.
   *
   * **Example**:
   * ```
   * 123abc
   * ```
   */
  clientUniqueId: string;

  /**
   * Direction
   *
   */
  direction: 'Conference' | 'Inbound' | 'Outbound' | 'Unknown';

  /**
   * Parties
   *
   * **Example**:
   * ```
   * [
   *    {
   *      "emailAddress": "test@test.com",
   *      "name": "Test User",
   *      "userId": "7387268041252473322"
   *    }
   *  ]
   * ```
   */
  parties: string;

  /**
   * Primary User
   * The Gong internal user ID of the team member who hosted the call.
   *
   * **Example**:
   * ```
   * 7347263041252417409
   * ```
   */
  primaryUser: string;

  /**
   * Title
   * The title of the call. This title is available in the Gong system for indexing and search.
   *
   * **Example**:
   * ```
   * Call with Sales
   * ```
   */
  title?: string;

  /**
   * Download Media URL
   * The URL from which Gong can download the media file. Up to 1.5 GB.
   *
   */
  downloadMediaUrl?: string;

  /**
   * Purpose
   * The purpose of the call. Limit of 255 characters.
   *
   */
  purpose?: string;

  /**
   * Duration
   * The call duration in seconds.
   *
   * **Example**:
   * ```
   * 3600.0
   * ```
   */
  duration?: string;
};

/**
 * Get Call by ID intent type
 */
type GongGetCallById = {
  /**
   * Call ID
   * Specify Gong's unique numeric identifier for the call (up to 20 digits).
   *
   * **Example**:
   * ```
   * 16470009016529344
   * ```
   */
  id: string;
};

/**
 * Search for Call intent type
 */
type GongSearchCall = {
  /**
   * Start Date
   * Accepts ISO 8601 date format.
   *
   * **Example**:
   * ```
   * 2022-08-01T07:00:00.000Z
   * ```
   */
  fromDateTime: string;

  /**
   * End Date
   * Accepts ISO 8601 date format.
   *
   * **Example**:
   * ```
   * 2022-08-01T07:00:00.000Z
   * ```
   */
  toDateTime: string;
};

export type IntentToInputOutput = {
  /**
   * GONG_ADD_CALL
   */
  GONG_ADD_CALL: [GongAddCall, any];

  /**
   * GONG_GET_CALL_BY_ID
   */
  GONG_GET_CALL_BY_ID: [GongGetCallById, any];

  /**
   * GONG_SEARCH_CALL
   */
  GONG_SEARCH_CALL: [GongSearchCall, any];
};
