import { ActionStep, ActionTriggerStep } from '@useparagon/core';
import {
  CustomDropdownField,
  DynamicMappingField,
} from '@useparagon/core/inputs';
import { IntegrationOptions } from '@useparagon/core/integration';

import { IntentToInputOutput } from './intents';
import { IntentToInputOutput as TriggerIntentToInputOutput } from './triggers';

type IGoogledriveIntegrationUserDefinedInput = {
  id: string;
  title: string;
  required?: boolean;
  tooltip?: string;
} & (
  | {
      type:
        | 'text'
        | 'number'
        | 'email'
        | 'url'
        | 'boolean'
        | 'password'
        | 'folder';
    }
  | {
      type: 'custom_dropdown';
      key: string;
      customDropdownOptions?: CustomDropdownField[];
    }
  | {
      type: 'custom_field_mapping';
      objectName: string;
      // these object types values will be used in preview portal
      mockObjectTypes?: DynamicMappingField[];
      // these integration fields values will be used in preview portal
      mockIntegrationFields?: DynamicMappingField[];
    }
);

/**
 * map b/w input type to result
 */
export type InputResultMap = {
  email: string;
  url: string;
  password: string;
  boolean: boolean;
  text: string;
  number: number;
  custom_dropdown: string;
  custom_field_mapping: {
    object: string;
    fields: Record<string, any>;
  };
  folder: string;
};

/**
 * pass { variableName for input => Input }
 * this should be used whenever we are creating inputs for integrations/workflows
 * as it will infer proper types
 */
export declare const createInputs: <
  T extends Record<string, IGoogledriveIntegrationUserDefinedInput>,
>(
  inputs: T,
) => T;

/**
 * integration type for googledrive
 */
export type IGoogledriveIntegration = {
  /**
   * name of integration
   */
  name: string;

  /**
   * action step methods
   */
  actions: {
    /**
     * Get File Metadata
     * method for GOOGLE_DRIVE_GET_FILE_BY_ID
     */
    googleDriveGetFileById: (
      inputs: IntentToInputOutput['GOOGLE_DRIVE_GET_FILE_BY_ID'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['GOOGLE_DRIVE_GET_FILE_BY_ID'][0],
      IntentToInputOutput['GOOGLE_DRIVE_GET_FILE_BY_ID'][1]
    >;

    /**
     * Upload File
     * method for GOOGLE_DRIVE_SAVE_FILE
     */
    googleDriveSaveFile: (
      inputs: IntentToInputOutput['GOOGLE_DRIVE_SAVE_FILE'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['GOOGLE_DRIVE_SAVE_FILE'][0],
      IntentToInputOutput['GOOGLE_DRIVE_SAVE_FILE'][1]
    >;

    /**
     * Download Google Doc
     * method for GOOGLE_DRIVE_EXPORT_DOC
     */
    googleDriveExportDoc: (
      inputs: IntentToInputOutput['GOOGLE_DRIVE_EXPORT_DOC'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['GOOGLE_DRIVE_EXPORT_DOC'][0],
      IntentToInputOutput['GOOGLE_DRIVE_EXPORT_DOC'][1]
    >;

    /**
     * Create Folder
     * method for GOOGLE_DRIVE_CREATE_FOLDER
     */
    googleDriveCreateFolder: (
      inputs: IntentToInputOutput['GOOGLE_DRIVE_CREATE_FOLDER'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['GOOGLE_DRIVE_CREATE_FOLDER'][0],
      IntentToInputOutput['GOOGLE_DRIVE_CREATE_FOLDER'][1]
    >;

    /**
     * Delete Folder
     * method for GOOGLE_DRIVE_DELETE_FOLDER
     */
    googleDriveDeleteFolder: (
      inputs: IntentToInputOutput['GOOGLE_DRIVE_DELETE_FOLDER'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['GOOGLE_DRIVE_DELETE_FOLDER'][0],
      IntentToInputOutput['GOOGLE_DRIVE_DELETE_FOLDER'][1]
    >;

    /**
     * Get Folder by ID
     * method for GOOGLE_DRIVE_GET_FOLDER_BY_ID
     */
    googleDriveGetFolderById: (
      inputs: IntentToInputOutput['GOOGLE_DRIVE_GET_FOLDER_BY_ID'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['GOOGLE_DRIVE_GET_FOLDER_BY_ID'][0],
      IntentToInputOutput['GOOGLE_DRIVE_GET_FOLDER_BY_ID'][1]
    >;

    /**
     * Move Folder
     * method for GOOGLE_DRIVE_MOVE_FOLDER
     */
    googleDriveMoveFolder: (
      inputs: IntentToInputOutput['GOOGLE_DRIVE_MOVE_FOLDER'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['GOOGLE_DRIVE_MOVE_FOLDER'][0],
      IntentToInputOutput['GOOGLE_DRIVE_MOVE_FOLDER'][1]
    >;

    /**
     * Get Files
     * method for GOOGLE_DRIVE_LIST_FILES
     */
    googleDriveListFiles: (
      inputs: IntentToInputOutput['GOOGLE_DRIVE_LIST_FILES'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['GOOGLE_DRIVE_LIST_FILES'][0],
      IntentToInputOutput['GOOGLE_DRIVE_LIST_FILES'][1]
    >;

    /**
     * Search Folders
     * method for GOOGLE_DRIVE_SEARCH_FOLDERS
     */
    googleDriveSearchFolders: (
      inputs: IntentToInputOutput['GOOGLE_DRIVE_SEARCH_FOLDERS'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['GOOGLE_DRIVE_SEARCH_FOLDERS'][0],
      IntentToInputOutput['GOOGLE_DRIVE_SEARCH_FOLDERS'][1]
    >;
  };

  /**
   * trigger step methods
   */
  triggers: {
    /**
     * File Created
     * method for GOOGLE_DRIVE_TRIGGER_FILE_CREATED
     */
    googleDriveTriggerFileCreated: (
      inputs: TriggerIntentToInputOutput['GOOGLE_DRIVE_TRIGGER_FILE_CREATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['GOOGLE_DRIVE_TRIGGER_FILE_CREATED'][0],
      TriggerIntentToInputOutput['GOOGLE_DRIVE_TRIGGER_FILE_CREATED'][1]
    >;

    /**
     * File Updated
     * method for GOOGLE_DRIVE_TRIGGER_FILE_UPDATED
     */
    googleDriveTriggerFileUpdated: (
      inputs: TriggerIntentToInputOutput['GOOGLE_DRIVE_TRIGGER_FILE_UPDATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['GOOGLE_DRIVE_TRIGGER_FILE_UPDATED'][0],
      TriggerIntentToInputOutput['GOOGLE_DRIVE_TRIGGER_FILE_UPDATED'][1]
    >;

    /**
     * File Deleted
     * method for GOOGLE_DRIVE_TRIGGER_FILE_DELETED
     */
    googleDriveTriggerFileDeleted: (
      inputs: TriggerIntentToInputOutput['GOOGLE_DRIVE_TRIGGER_FILE_DELETED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['GOOGLE_DRIVE_TRIGGER_FILE_DELETED'][0],
      TriggerIntentToInputOutput['GOOGLE_DRIVE_TRIGGER_FILE_DELETED'][1]
    >;
  };
};
