import { ActionStep, ActionTriggerStep } from '@useparagon/core';
import {
  CustomDropdownField,
  DynamicMappingField,
} from '@useparagon/core/inputs';
import { IntegrationOptions } from '@useparagon/core/integration';

import { IntentToInputOutput } from './intents';
import { IntentToInputOutput as TriggerIntentToInputOutput } from './triggers';

type ILeverIntegrationUserDefinedInput = {
  id: string;
  title: string;
  required?: boolean;
  tooltip?: string;
} & (
  | {
      type: 'text' | 'number' | 'email' | 'url' | 'boolean' | 'password';
    }
  | {
      type: 'custom_dropdown';
      key: string;
      customDropdownOptions?: CustomDropdownField[];
    }
  | {
      type: 'custom_field_mapping';
      objectName: string;
      // these object types values will be used in preview portal
      mockObjectTypes?: DynamicMappingField[];
      // these integration fields values will be used in preview portal
      mockIntegrationFields?: DynamicMappingField[];
    }
);

/**
 * map b/w input type to result
 */
export type InputResultMap = {
  email: string;
  url: string;
  password: string;
  boolean: boolean;
  text: string;
  number: number;
  custom_dropdown: string;
  custom_field_mapping: {
    object: string;
    fields: Record<string, any>;
  };
};

/**
 * pass { variableName for input => Input }
 * this should be used whenever we are creating inputs for integrations/workflows
 * as it will infer proper types
 */
export declare const createInputs: <
  T extends Record<string, ILeverIntegrationUserDefinedInput>,
>(
  inputs: T,
) => T;

/**
 * integration type for lever
 */
export type ILeverIntegration = {
  /**
   * name of integration
   */
  name: string;

  /**
   * action step methods
   */
  actions: {
    /**
     * Create a Opportunity
     * method for LEVER_CREATE_OPPORTUNITY
     */
    createOpportunity: (
      inputs: IntentToInputOutput['LEVER_CREATE_OPPORTUNITY'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['LEVER_CREATE_OPPORTUNITY'][0],
      IntentToInputOutput['LEVER_CREATE_OPPORTUNITY'][1]
    >;

    /**
     * Get Opportunity by ID
     * method for LEVER_GET_OPPORTUNITY_BY_ID
     */
    getOpportunityById: (
      inputs: IntentToInputOutput['LEVER_GET_OPPORTUNITY_BY_ID'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['LEVER_GET_OPPORTUNITY_BY_ID'][0],
      IntentToInputOutput['LEVER_GET_OPPORTUNITY_BY_ID'][1]
    >;

    /**
     * Get Opportunities
     * method for LEVER_GET_OPPORTUNITIES
     */
    getOpportunities: (
      inputs: IntentToInputOutput['LEVER_GET_OPPORTUNITIES'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['LEVER_GET_OPPORTUNITIES'][0],
      IntentToInputOutput['LEVER_GET_OPPORTUNITIES'][1]
    >;

    /**
     * Update Contact
     * method for LEVER_UPDATE_CONTACT
     */
    updateContact: (
      inputs: IntentToInputOutput['LEVER_UPDATE_CONTACT'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['LEVER_UPDATE_CONTACT'][0],
      IntentToInputOutput['LEVER_UPDATE_CONTACT'][1]
    >;

    /**
     * Get Contact by ID
     * method for LEVER_GET_CONTACT_BY_ID
     */
    getContactById: (
      inputs: IntentToInputOutput['LEVER_GET_CONTACT_BY_ID'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['LEVER_GET_CONTACT_BY_ID'][0],
      IntentToInputOutput['LEVER_GET_CONTACT_BY_ID'][1]
    >;

    /**
     * Create a Posting
     * method for LEVER_CREATE_POSTING
     */
    createPosting: (
      inputs: IntentToInputOutput['LEVER_CREATE_POSTING'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['LEVER_CREATE_POSTING'][0],
      IntentToInputOutput['LEVER_CREATE_POSTING'][1]
    >;

    /**
     * Update Posting
     * method for LEVER_UPDATE_POSTING
     */
    updatePosting: (
      inputs: IntentToInputOutput['LEVER_UPDATE_POSTING'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['LEVER_UPDATE_POSTING'][0],
      IntentToInputOutput['LEVER_UPDATE_POSTING'][1]
    >;

    /**
     * Get Posting by ID
     * method for LEVER_GET_POSTING_BY_ID
     */
    getPostingById: (
      inputs: IntentToInputOutput['LEVER_GET_POSTING_BY_ID'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['LEVER_GET_POSTING_BY_ID'][0],
      IntentToInputOutput['LEVER_GET_POSTING_BY_ID'][1]
    >;

    /**
     * Get Postings
     * method for LEVER_GET_POSTINGS
     */
    getPostings: (
      inputs: IntentToInputOutput['LEVER_GET_POSTINGS'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['LEVER_GET_POSTINGS'][0],
      IntentToInputOutput['LEVER_GET_POSTINGS'][1]
    >;
  };

  /**
   * trigger step methods
   */
  triggers: {
    /**
     * Candidate Created
     * method for LEVER_TRIGGER_CANDIDATE_CREATED
     */
    candidateCreated: (
      inputs: TriggerIntentToInputOutput['LEVER_TRIGGER_CANDIDATE_CREATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['LEVER_TRIGGER_CANDIDATE_CREATED'][0],
      TriggerIntentToInputOutput['LEVER_TRIGGER_CANDIDATE_CREATED'][1]
    >;

    /**
     * Candidate Updated
     * method for LEVER_TRIGGER_CANDIDATE_UPDATED
     */
    candidateUpdated: (
      inputs: TriggerIntentToInputOutput['LEVER_TRIGGER_CANDIDATE_UPDATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['LEVER_TRIGGER_CANDIDATE_UPDATED'][0],
      TriggerIntentToInputOutput['LEVER_TRIGGER_CANDIDATE_UPDATED'][1]
    >;

    /**
     * Posting Created
     * method for LEVER_TRIGGER_POSTING_CREATED
     */
    postingCreated: (
      inputs: TriggerIntentToInputOutput['LEVER_TRIGGER_POSTING_CREATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['LEVER_TRIGGER_POSTING_CREATED'][0],
      TriggerIntentToInputOutput['LEVER_TRIGGER_POSTING_CREATED'][1]
    >;

    /**
     * Posting Updated
     * method for LEVER_TRIGGER_POSTING_UPDATED
     */
    postingUpdated: (
      inputs: TriggerIntentToInputOutput['LEVER_TRIGGER_POSTING_UPDATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['LEVER_TRIGGER_POSTING_UPDATED'][0],
      TriggerIntentToInputOutput['LEVER_TRIGGER_POSTING_UPDATED'][1]
    >;
  };
};
