import { ConditionalInput } from '@useparagon/core/steps/library/conditional';

/**
 * Create Issue intent type
 */
type LinearCreateIssue = {
  /**
   * Team ID
   * Specify the Team ID of the parent for this new issue. Use Connect Portal Workflow Settings to allow users to select a Team ID.
   *
   * **Example**:
   * ```
   * a70bdf0f-530a-4887-857d-46151b52b47c
   * ```
   */
  teamId: string;

  /**
   * Title
   * Specify a title for this issue.
   *
   */
  title: string;

  /**
   * Description
   * Specify a description for this issue.
   *
   */
  description?: string;

  /**
   * Status
   * Specify the state or status of this issue.
   *
   */
  statusId?: string;

  /**
   * Priority
   * Specify the priority of this issue as an integer.
   *
   */
  priority?: string;

  /**
   * Due Date
   * Specify the due date of this issue in ISO 8601 format.
   *
   */
  dueDate?: string;

  /**
   * Cycle ID
   * Specify the cycle associated with this issue.
   *
   */
  cycleId?: string;

  /**
   * Additional Fields
   *
   * **Example**:
   * ```
   * {
   *  assigneeId: "a70bdf0f-530a-4887-857d-46151b52b47c",
   *  labelIds: ["a70bdf0f-530a-4887-857d-46151b52b47c"]
   *}
   * ```
   */
  additionalFields?: string;
};

