import { ActionStep } from '@useparagon/core';
import {
  CustomDropdownField,
  DynamicMappingField,
} from '@useparagon/core/inputs';
import { IntegrationOptions } from '@useparagon/core/integration';

import { IntentToInputOutput } from './intents';

type ILinkedinIntegrationUserDefinedInput = {
  id: string;
  title: string;
  required?: boolean;
  tooltip?: string;
} & (
  | {
      type: 'text' | 'number' | 'email' | 'url' | 'boolean' | 'password';
    }
  | {
      type: 'custom_dropdown';
      key: string;
      customDropdownOptions?: CustomDropdownField[];
    }
  | {
      type: 'custom_field_mapping';
      objectName: string;
      // these object types values will be used in preview portal
      mockObjectTypes?: DynamicMappingField[];
      // these integration fields values will be used in preview portal
      mockIntegrationFields?: DynamicMappingField[];
    }
);

/**
 * map b/w input type to result
 */
export type InputResultMap = {
  email: string;
  url: string;
  password: string;
  boolean: boolean;
  text: string;
  number: number;
  custom_dropdown: string;
  custom_field_mapping: {
    object: string;
    fields: Record<string, any>;
  };
};

/**
 * pass { variableName for input => Input }
 * this should be used whenever we are creating inputs for integrations/workflows
 * as it will infer proper types
 */
export declare const createInputs: <
  T extends Record<string, ILinkedinIntegrationUserDefinedInput>,
>(
  inputs: T,
) => T;

/**
 * integration type for linkedin
 */
export type ILinkedinIntegration = {
  /**
   * name of integration
   */
  name: string;

  /**
   * action step methods
   */
  actions: {
    /**
     * Get Profile By ID
     * method for LINKEDIN_GET_PROFILE_BY_ID
     */
    getProfileById: (
      inputs: IntentToInputOutput['LINKEDIN_GET_PROFILE_BY_ID'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['LINKEDIN_GET_PROFILE_BY_ID'][0],
      IntentToInputOutput['LINKEDIN_GET_PROFILE_BY_ID'][1]
    >;

    /**
     * Create Post
     * method for LINKEDIN_CREATE_POST
     */
    createPost: (
      inputs: IntentToInputOutput['LINKEDIN_CREATE_POST'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['LINKEDIN_CREATE_POST'][0],
      IntentToInputOutput['LINKEDIN_CREATE_POST'][1]
    >;
  };

  /**
   * trigger step methods
   */
  triggers: {};
};
