/**
 * Get Profile By ID intent type
 */
type LinkedinGetProfileById = {
  /**
   * Profile ID
   * Specify the ID of the profile to fetch.
   *
   */
  id: string;
};

/**
 * Create Post intent type
 */
type LinkedinCreatePost = {
  /**
   * Author
   * Specify the Author who is creating the post.
   *
   * **Example**:
   * ```
   * person:-OLWJgnReo
   * ```
   */
  author: string;

  /**
   * Life Cycle State
   * Specify the state of the post, you can use "PUBLISHED" to make it visible to others.
   *
   */
  lifecycleState: 'PUBLISHED' | 'DRAFT';

  /**
   * Text
   * Specify the text of the post.
   *
   */
  text: string;

  /**
   * Visibility
   * Specify the visibility of the post.
   *
   */
  visibility: 'CONNECTIONS' | 'PUBLIC';
};

export type IntentToInputOutput = {
  /**
   * LINKEDIN_GET_PROFILE_BY_ID
   */
  LINKEDIN_GET_PROFILE_BY_ID: [LinkedinGetProfileById, any];

  /**
   * LINKEDIN_CREATE_POST
   */
  LINKEDIN_CREATE_POST: [LinkedinCreatePost, any];
};
