import { ActionStep, ActionTriggerStep } from '@useparagon/core';
import {
  CustomDropdownField,
  DynamicMappingField,
} from '@useparagon/core/inputs';
import { IntegrationOptions } from '@useparagon/core/integration';

import { IntentToInputOutput } from './intents';
import { IntentToInputOutput as TriggerIntentToInputOutput } from './triggers';

type IMailchimpIntegrationUserDefinedInput = {
  id: string;
  title: string;
  required?: boolean;
  tooltip?: string;
} & (
  | {
      type:
        | 'text'
        | 'number'
        | 'email'
        | 'url'
        | 'boolean'
        | 'password'
        | 'list'
        | 'template';
    }
  | {
      type: 'custom_dropdown';
      key: string;
      customDropdownOptions?: CustomDropdownField[];
    }
  | {
      type: 'custom_field_mapping';
      objectName: string;
      // these object types values will be used in preview portal
      mockObjectTypes?: DynamicMappingField[];
      // these integration fields values will be used in preview portal
      mockIntegrationFields?: DynamicMappingField[];
    }
);

/**
 * map b/w input type to result
 */
export type InputResultMap = {
  email: string;
  url: string;
  password: string;
  boolean: boolean;
  text: string;
  number: number;
  custom_dropdown: string;
  custom_field_mapping: {
    object: string;
    fields: Record<string, any>;
  };
  list: string;
  template: string;
};

/**
 * pass { variableName for input => Input }
 * this should be used whenever we are creating inputs for integrations/workflows
 * as it will infer proper types
 */
export declare const createInputs: <
  T extends Record<string, IMailchimpIntegrationUserDefinedInput>,
>(
  inputs: T,
) => T;

/**
 * integration type for mailchimp
 */
export type IMailchimpIntegration = {
  /**
   * name of integration
   */
  name: string;

  /**
   * action step methods
   */
  actions: {
    /**
     * Create Campaign
     * method for MAILCHIMP_CREATE_CAMPAIGN
     */
    createCampaign: (
      inputs: IntentToInputOutput['MAILCHIMP_CREATE_CAMPAIGN'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['MAILCHIMP_CREATE_CAMPAIGN'][0],
      IntentToInputOutput['MAILCHIMP_CREATE_CAMPAIGN'][1]
    >;

    /**
     * Update Campaign
     * method for MAILCHIMP_UPDATE_CAMPAIGN
     */
    updateCampaign: (
      inputs: IntentToInputOutput['MAILCHIMP_UPDATE_CAMPAIGN'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['MAILCHIMP_UPDATE_CAMPAIGN'][0],
      IntentToInputOutput['MAILCHIMP_UPDATE_CAMPAIGN'][1]
    >;

    /**
     * Send Campaign
     * method for MAILCHIMP_SEND_CAMPAIGN
     */
    sendCampaign: (
      inputs: IntentToInputOutput['MAILCHIMP_SEND_CAMPAIGN'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['MAILCHIMP_SEND_CAMPAIGN'][0],
      IntentToInputOutput['MAILCHIMP_SEND_CAMPAIGN'][1]
    >;

    /**
     * Search Campaigns
     * method for MAILCHIMP_SEARCH_CAMPAIGNS
     */
    searchCampaigns: (
      inputs: IntentToInputOutput['MAILCHIMP_SEARCH_CAMPAIGNS'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['MAILCHIMP_SEARCH_CAMPAIGNS'][0],
      IntentToInputOutput['MAILCHIMP_SEARCH_CAMPAIGNS'][1]
    >;

    /**
     * Get Campaign by ID
     * method for MAILCHIMP_GET_CAMPAIGN_BY_ID
     */
    getCampaignById: (
      inputs: IntentToInputOutput['MAILCHIMP_GET_CAMPAIGN_BY_ID'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['MAILCHIMP_GET_CAMPAIGN_BY_ID'][0],
      IntentToInputOutput['MAILCHIMP_GET_CAMPAIGN_BY_ID'][1]
    >;

    /**
     * Delete Campaign by ID
     * method for MAILCHIMP_DELETE_CAMPAIGN_BY_ID
     */
    deleteCampaignById: (
      inputs: IntentToInputOutput['MAILCHIMP_DELETE_CAMPAIGN_BY_ID'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['MAILCHIMP_DELETE_CAMPAIGN_BY_ID'][0],
      IntentToInputOutput['MAILCHIMP_DELETE_CAMPAIGN_BY_ID'][1]
    >;

    /**
     * Create List
     * method for MAILCHIMP_CREATE_LIST
     */
    createList: (
      inputs: IntentToInputOutput['MAILCHIMP_CREATE_LIST'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['MAILCHIMP_CREATE_LIST'][0],
      IntentToInputOutput['MAILCHIMP_CREATE_LIST'][1]
    >;

    /**
     * Get List by ID
     * method for MAILCHIMP_GET_LIST_BY_ID
     */
    getListById: (
      inputs: IntentToInputOutput['MAILCHIMP_GET_LIST_BY_ID'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['MAILCHIMP_GET_LIST_BY_ID'][0],
      IntentToInputOutput['MAILCHIMP_GET_LIST_BY_ID'][1]
    >;

    /**
     * Search Lists
     * method for MAILCHIMP_SEARCH_LISTS
     */
    searchLists: (
      inputs: IntentToInputOutput['MAILCHIMP_SEARCH_LISTS'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['MAILCHIMP_SEARCH_LISTS'][0],
      IntentToInputOutput['MAILCHIMP_SEARCH_LISTS'][1]
    >;

    /**
     * Add Contact to List
     * method for MAILCHIMP_ADD_CONTACT_TO_LIST
     */
    addContactToList: (
      inputs: IntentToInputOutput['MAILCHIMP_ADD_CONTACT_TO_LIST'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['MAILCHIMP_ADD_CONTACT_TO_LIST'][0],
      IntentToInputOutput['MAILCHIMP_ADD_CONTACT_TO_LIST'][1]
    >;

    /**
     * Update Contact in List
     * method for MAILCHIMP_UPDATE_CONTACT_TO_LIST
     */
    updateContactToList: (
      inputs: IntentToInputOutput['MAILCHIMP_UPDATE_CONTACT_TO_LIST'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['MAILCHIMP_UPDATE_CONTACT_TO_LIST'][0],
      IntentToInputOutput['MAILCHIMP_UPDATE_CONTACT_TO_LIST'][1]
    >;

    /**
     * Get Contacts from List
     * method for MAILCHIMP_GET_CONTACTS_FROM_LIST
     */
    getContactsFromList: (
      inputs: IntentToInputOutput['MAILCHIMP_GET_CONTACTS_FROM_LIST'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['MAILCHIMP_GET_CONTACTS_FROM_LIST'][0],
      IntentToInputOutput['MAILCHIMP_GET_CONTACTS_FROM_LIST'][1]
    >;
  };

  /**
   * trigger step methods
   */
  triggers: {
    /**
     * Mailchimp Lists
     * method for MAILCHIMP_TRIGGER_LIST
     */
    list: (
      inputs: TriggerIntentToInputOutput['MAILCHIMP_TRIGGER_LIST'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['MAILCHIMP_TRIGGER_LIST'][0],
      TriggerIntentToInputOutput['MAILCHIMP_TRIGGER_LIST'][1]
    >;
  };
};
