import { ConditionalInput } from '@useparagon/core/steps/library/conditional';

/**
 * Create Campaign intent type
 */
type MailchimpCreateCampaign = {
  /**
   * Title
   * The title to describe this campaign in your Mailchimp account.
   *
   */
  title: string;

  /**
   * List ID
   * Select a list to send this campaign to. Use Connect Portal Workflow Settings to allow users to select a List.
   *
   * **Example**:
   * ```
   * {{settings.list}}
   * ```
   */
  listId: string;

  /**
   * From Name
   * The “From” name for this campaign (not an email address).
   *
   */
  fromName: string;

  /**
   * Reply-To Email Address
   * The reply-to address for the email sent with this campaign.
   *
   */
  replyTo: string;

  /**
   * Subject Line
   * The subject line to be used in the email sent with this campaign.
   *
   */
  subjectLine: string;

  /**
   * Preview Text
   * The preview text for the email of this campaign.
   *
   */
  previewText?: string;

  /**
   * Template ID
   * The template to use in the email for this campaign. Use Connect Portal Workflow Settings to select a Template.
   *
   * **Example**:
   * ```
   * {{settings.template}}
   * ```
   */
  templateId?: string;
};

