import { ActionStep, ActionTriggerStep } from '@useparagon/core';
import {
  CustomDropdownField,
  UserDefinedFieldMappingOptions,
} from '@useparagon/core/inputs';
import { IntegrationOptions } from '@useparagon/core/integration';

import { IntentToInputOutput } from './intents';
import { IntentToInputOutput as TriggerIntentToInputOutput } from './triggers';

type IMarketoIntegrationUserDefinedInput = {
  id: string;
  title: string;
  required?: boolean;
  tooltip?: string;
} & (
  | {
      type:
        | 'text'
        | 'number'
        | 'email'
        | 'url'
        | 'boolean'
        | 'password'
        | 'list';
    }
  | {
      type: 'custom_dropdown';
      key: string;
      customDropdownOptions?: CustomDropdownField[];
    }
  | ({
      type: 'field_mapping';
    } & UserDefinedFieldMappingOptions)
);

/**
 * map b/w input type to result
 */
export type InputResultMap = {
  email: string;
  url: string;
  password: string;
  boolean: boolean;
  text: string;
  number: number;
  custom_dropdown: string;

  list: string;
  field_mapping: {
    custom_objects: string;
    custom_object_fields: Record<string, any>;
  };
};

/**
 * pass { variableName for input => Input }
 * this should be used whenever we are creating inputs for integrations/workflows
 * as it will infer proper types
 */
export declare const createInputs: <
  T extends Record<string, IMarketoIntegrationUserDefinedInput>,
>(
  inputs: T,
) => T;

/**
 * integration type for marketo
 */
export type IMarketoIntegration = {
  /**
   * name of integration
   */
  name: string;

  /**
   * action step methods
   */
  actions: {
    /**
     * Create or Update Lead
     * method for MARKETO_CREATE_OR_UPDATE_LEAD
     */
    createOrUpdateLead: (
      inputs: IntentToInputOutput['MARKETO_CREATE_OR_UPDATE_LEAD'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['MARKETO_CREATE_OR_UPDATE_LEAD'][0],
      IntentToInputOutput['MARKETO_CREATE_OR_UPDATE_LEAD'][1]
    >;

    /**
     * Get Leads
     * method for MARKETO_GET_LEADS
     */
    getLeads: (
      inputs: IntentToInputOutput['MARKETO_GET_LEADS'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['MARKETO_GET_LEADS'][0],
      IntentToInputOutput['MARKETO_GET_LEADS'][1]
    >;

    /**
     * Get Lead by ID
     * method for MARKETO_GET_LEAD_BY_ID
     */
    getLeadById: (
      inputs: IntentToInputOutput['MARKETO_GET_LEAD_BY_ID'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['MARKETO_GET_LEAD_BY_ID'][0],
      IntentToInputOutput['MARKETO_GET_LEAD_BY_ID'][1]
    >;

    /**
     * Add Leads to List
     * method for MARKETO_ADD_LEADS_TO_LIST
     */
    addLeadsToList: (
      inputs: IntentToInputOutput['MARKETO_ADD_LEADS_TO_LIST'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['MARKETO_ADD_LEADS_TO_LIST'][0],
      IntentToInputOutput['MARKETO_ADD_LEADS_TO_LIST'][1]
    >;

    /**
     * Create Custom Object
     * method for MARKETO_CREATE_CUSTOM_OBJECT
     */
    createCustomObject: (
      inputs: IntentToInputOutput['MARKETO_CREATE_CUSTOM_OBJECT'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['MARKETO_CREATE_CUSTOM_OBJECT'][0],
      IntentToInputOutput['MARKETO_CREATE_CUSTOM_OBJECT'][1]
    >;
  };

  /**
   * trigger step methods
   */
  triggers: {
    /**
     * Lead Added to a list
     * method for MARKETO_TRIGGER_LEAD_ADDED_TO_LIST
     */
    leadAddedToList: (
      inputs: TriggerIntentToInputOutput['MARKETO_TRIGGER_LEAD_ADDED_TO_LIST'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['MARKETO_TRIGGER_LEAD_ADDED_TO_LIST'][0],
      TriggerIntentToInputOutput['MARKETO_TRIGGER_LEAD_ADDED_TO_LIST'][1]
    >;
  };
};
