import { ConditionalInput } from '@useparagon/core/steps/library/conditional';

/**
 * Create or Update Lead intent type
 */
type MarketoCreateOrUpdateLead = {
  /**
   * Email
   * If there already exists a lead with this email, it will be updated.
   *
   */
  email: string;

  /**
   * First Name
   *
   */
  firstName?: string;

  /**
   * Last Name
   *
   */
  lastName?: string;

  /**
   * Company
   *
   */
  company?: string;

  /**
   * Title
   *
   */
  title?: string;

  /**
   * Phone
   *
   */
  phone?: string;

  /**
   * Website
   *
   */
  website?: string;

  /**
   * Additional Fields
   * Specify any other fields that should be updated in JSON below.
   *
   * **Example**:
   * ```
   * {
   *"property_number": "17",
   *"property_dropdown": "choice_b",
   *"property_radio": "option_1",
   *"property_string": "value",
   *"property_date": "1572480000000"
   *}
   * ```
   */
  additionalFields?: string;
};

/**
 * Get Leads intent type
 */
type MarketoGetLeads = {
  /**
   * Filter search
   * Search for records that match specified filters.
   *
   */
  filterFormula?: ConditionalInput;
};

/**
 * Get Lead by ID intent type
 */
type MarketoGetLeadById = {
  /**
   * Lead ID
   *
   */
  leadId: string;
};

/**
 * Add Leads to List intent type
 */
type MarketoAddLeadsToList = {
  /**
   * Lead IDs
   * IDs of leads to be added to the List (up to 300). You can enter a single ID or an array of IDs.
   *
   */
  leadIds: string;

  /**
   * List
   * The List that the lead(s) should be added to. Use Connect Portal Workflow Settings to allow users to select a List.
   *
   * **Example**:
   * ```
   * {{settings.list}}
   * ```
   */
  listId?: string;
};

/**
 * Create Custom Object intent type
 */
type MarketoCreateCustomObject = {
  /**
   * Object Name
   * The API Name of the Object to create. Use Connect Portal Workflow Settings to allow users to select a Custom Object Mapping.
   *
   * **Example**:
   * ```
   * {{settings.objectType}}
   * ```
   */
  customObjectName: string;

  /**
   * Record Fields
   * Use Connect Portal Workflow Settings to allow users to select a Custom Object Mapping to specify which fields to update.
   *
   * **Example**:
   * ```
   * {
   *"property_number": "17",
   *"property_dropdown": "choice_b",
   *"property_radio": "option_1",
   *"property_string": "value",
   *"property_date": "1572480000000"
   *}
   * ```
   */
  recordFields: string;
};

export type IntentToInputOutput = {
  /**
   * MARKETO_CREATE_OR_UPDATE_LEAD
   */
  MARKETO_CREATE_OR_UPDATE_LEAD: [MarketoCreateOrUpdateLead, any];

  /**
   * MARKETO_GET_LEADS
   */
  MARKETO_GET_LEADS: [MarketoGetLeads, any];

  /**
   * MARKETO_GET_LEAD_BY_ID
   */
  MARKETO_GET_LEAD_BY_ID: [MarketoGetLeadById, any];

  /**
   * MARKETO_ADD_LEADS_TO_LIST
   */
  MARKETO_ADD_LEADS_TO_LIST: [MarketoAddLeadsToList, any];

  /**
   * MARKETO_CREATE_CUSTOM_OBJECT
   */
  MARKETO_CREATE_CUSTOM_OBJECT: [MarketoCreateCustomObject, any];
};
