import { ActionStep, ActionTriggerStep } from '@useparagon/core';
import {
  CustomDropdownField,
  DynamicMappingField,
  UserDefinedComboInputOptions,
} from '@useparagon/core/inputs';
import { IntegrationOptions } from '@useparagon/core/integration';

import { IntentToInputOutput } from './intents';
import { IntentToInputOutput as TriggerIntentToInputOutput } from './triggers';

type IMicrosoftTeamsIntegrationUserDefinedInput = {
  id: string;
  title: string;
  required?: boolean;
  tooltip?: string;
} & (
  | {
      type:
        | 'text'
        | 'number'
        | 'email'
        | 'url'
        | 'boolean'
        | 'password'
        | 'member';
    }
  | {
      type: 'custom_dropdown';
      key: string;
      customDropdownOptions?: CustomDropdownField[];
    }
  | ({
      type: 'channel';
    } & UserDefinedComboInputOptions)
  | {
      type: 'custom_field_mapping';
      objectName: string;
      // these object types values will be used in preview portal
      mockObjectTypes?: DynamicMappingField[];
      // these integration fields values will be used in preview portal
      mockIntegrationFields?: DynamicMappingField[];
    }
);

/**
 * map b/w input type to result
 */
export type InputResultMap = {
  email: string;
  url: string;
  password: string;
  boolean: boolean;
  text: string;
  number: number;
  custom_dropdown: string;
  custom_field_mapping: {
    object: string;
    fields: Record<string, any>;
  };
  member: string;
  channel: {
    teams: string;
    channel: string;
  };
};

/**
 * pass { variableName for input => Input }
 * this should be used whenever we are creating inputs for integrations/workflows
 * as it will infer proper types
 */
export declare const createInputs: <
  T extends Record<string, IMicrosoftTeamsIntegrationUserDefinedInput>,
>(
  inputs: T,
) => T;

/**
 * integration type for microsoftTeams
 */
export type IMicrosoftTeamsIntegration = {
  /**
   * name of integration
   */
  name: string;

  /**
   * action step methods
   */
  actions: {
    /**
     * Send message in channel
     * method for TEAMS_SEND_MESSAGE_IN_CHANNEL
     */
    teamsSendMessageInChannel: (
      inputs: IntentToInputOutput['TEAMS_SEND_MESSAGE_IN_CHANNEL'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['TEAMS_SEND_MESSAGE_IN_CHANNEL'][0],
      IntentToInputOutput['TEAMS_SEND_MESSAGE_IN_CHANNEL'][1]
    >;

    /**
     * Send message in chat
     * method for TEAMS_SEND_MESSAGE_IN_CHAT
     */
    teamsSendMessageInChat: (
      inputs: IntentToInputOutput['TEAMS_SEND_MESSAGE_IN_CHAT'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['TEAMS_SEND_MESSAGE_IN_CHAT'][0],
      IntentToInputOutput['TEAMS_SEND_MESSAGE_IN_CHAT'][1]
    >;

    /**
     * Get User by Email
     * method for TEAMS_GET_USER_BY_EMAIL
     */
    teamsGetUserByEmail: (
      inputs: IntentToInputOutput['TEAMS_GET_USER_BY_EMAIL'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['TEAMS_GET_USER_BY_EMAIL'][0],
      IntentToInputOutput['TEAMS_GET_USER_BY_EMAIL'][1]
    >;
  };

  /**
   * trigger step methods
   */
  triggers: {
    /**
     * Channel Created
     * method for TEAMS_TRIGGER_CHANNEL_CREATED
     */
    teamsTriggerChannelCreated: (
      inputs: TriggerIntentToInputOutput['TEAMS_TRIGGER_CHANNEL_CREATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['TEAMS_TRIGGER_CHANNEL_CREATED'][0],
      TriggerIntentToInputOutput['TEAMS_TRIGGER_CHANNEL_CREATED'][1]
    >;

    /**
     * Chat Created
     * method for TEAMS_TRIGGER_CHAT_CREATED
     */
    teamsTriggerChatCreated: (
      inputs: TriggerIntentToInputOutput['TEAMS_TRIGGER_CHAT_CREATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['TEAMS_TRIGGER_CHAT_CREATED'][0],
      TriggerIntentToInputOutput['TEAMS_TRIGGER_CHAT_CREATED'][1]
    >;

    /**
     * Chat Updated
     * method for TEAMS_TRIGGER_CHAT_UPDATED
     */
    teamsTriggerChatUpdated: (
      inputs: TriggerIntentToInputOutput['TEAMS_TRIGGER_CHAT_UPDATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['TEAMS_TRIGGER_CHAT_UPDATED'][0],
      TriggerIntentToInputOutput['TEAMS_TRIGGER_CHAT_UPDATED'][1]
    >;
  };
};
