import { ActionStep, ActionTriggerStep } from '@useparagon/core';
import {
  CustomDropdownField,
  UserDefinedFieldMappingOptions,
} from '@useparagon/core/inputs';
import { IntegrationOptions } from '@useparagon/core/integration';

import { IntentToInputOutput } from './intents';
import { IntentToInputOutput as TriggerIntentToInputOutput } from './triggers';

type IPipedriveIntegrationUserDefinedInput = {
  id: string;
  title: string;
  required?: boolean;
  tooltip?: string;
} & (
  | {
      type:
        | 'text'
        | 'number'
        | 'email'
        | 'url'
        | 'boolean'
        | 'password'
        | 'pipeline_(deprecated)'
        | 'pipeline'
        | 'stage_(deprecated)'
        | 'stage';
    }
  | {
      type: 'custom_dropdown';
      key: string;
      customDropdownOptions?: CustomDropdownField[];
    }
  | ({
      type: 'custom_field_mapping';
    } & UserDefinedFieldMappingOptions)
);

/**
 * map b/w input type to result
 */
export type InputResultMap = {
  email: string;
  url: string;
  password: string;
  boolean: boolean;
  text: string;
  number: number;
  custom_dropdown: string;

  'pipeline_(deprecated)': string;
  pipeline: string;
  'stage_(deprecated)': string;
  stage: string;
  custom_field_mapping: {
    custom_object_types: string;
    custom_object_field: Record<string, any>;
  };
};

/**
 * pass { variableName for input => Input }
 * this should be used whenever we are creating inputs for integrations/workflows
 * as it will infer proper types
 */
export declare const createInputs: <
  T extends Record<string, IPipedriveIntegrationUserDefinedInput>,
>(
  inputs: T,
) => T;

/**
 * integration type for pipedrive
 */
export type IPipedriveIntegration = {
  /**
   * name of integration
   */
  name: string;

  /**
   * action step methods
   */
  actions: {
    /**
     * Create Record
     * method for PIPEDRIVE_CREATE_RECORD
     */
    createRecord: (
      inputs: IntentToInputOutput['PIPEDRIVE_CREATE_RECORD'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['PIPEDRIVE_CREATE_RECORD'][0],
      IntentToInputOutput['PIPEDRIVE_CREATE_RECORD'][1]
    >;

    /**
     * Update Record
     * method for PIPEDRIVE_UPDATE_RECORD
     */
    updateRecord: (
      inputs: IntentToInputOutput['PIPEDRIVE_UPDATE_RECORD'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['PIPEDRIVE_UPDATE_RECORD'][0],
      IntentToInputOutput['PIPEDRIVE_UPDATE_RECORD'][1]
    >;

    /**
     * Get Record by ID
     * method for PIPEDRIVE_GET_RECORD_BY_ID
     */
    getRecordById: (
      inputs: IntentToInputOutput['PIPEDRIVE_GET_RECORD_BY_ID'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['PIPEDRIVE_GET_RECORD_BY_ID'][0],
      IntentToInputOutput['PIPEDRIVE_GET_RECORD_BY_ID'][1]
    >;

    /**
     * Get Records
     * method for PIPEDRIVE_GET_RECORDS
     */
    getRecords: (
      inputs: IntentToInputOutput['PIPEDRIVE_GET_RECORDS'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['PIPEDRIVE_GET_RECORDS'][0],
      IntentToInputOutput['PIPEDRIVE_GET_RECORDS'][1]
    >;

    /**
     * Delete Record
     * method for PIPEDRIVE_DELETE_RECORD
     */
    deleteRecord: (
      inputs: IntentToInputOutput['PIPEDRIVE_DELETE_RECORD'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['PIPEDRIVE_DELETE_RECORD'][0],
      IntentToInputOutput['PIPEDRIVE_DELETE_RECORD'][1]
    >;
  };

  /**
   * trigger step methods
   */
  triggers: {
    /**
     * Record Created
     * method for PIPEDRIVE_TRIGGER_RECORD_CREATED
     */
    recordCreated: (
      inputs: TriggerIntentToInputOutput['PIPEDRIVE_TRIGGER_RECORD_CREATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['PIPEDRIVE_TRIGGER_RECORD_CREATED'][0],
      TriggerIntentToInputOutput['PIPEDRIVE_TRIGGER_RECORD_CREATED'][1]
    >;

    /**
     * Record Updated
     * method for PIPEDRIVE_TRIGGER_RECORD_UPDATED
     */
    recordUpdated: (
      inputs: TriggerIntentToInputOutput['PIPEDRIVE_TRIGGER_RECORD_UPDATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['PIPEDRIVE_TRIGGER_RECORD_UPDATED'][0],
      TriggerIntentToInputOutput['PIPEDRIVE_TRIGGER_RECORD_UPDATED'][1]
    >;

    /**
     * Lead Created
     * method for PIPEDRIVE_TRIGGER_LEAD_CREATED
     */
    leadCreated: (
      inputs: TriggerIntentToInputOutput['PIPEDRIVE_TRIGGER_LEAD_CREATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['PIPEDRIVE_TRIGGER_LEAD_CREATED'][0],
      TriggerIntentToInputOutput['PIPEDRIVE_TRIGGER_LEAD_CREATED'][1]
    >;
  };
};
