/**
 * Create Ticket intent type
 */
type ServicenowCreateTicket = {
  /**
   * Ticket Type
   * Specify the type of the ticket to create.
   *
   */
  ticketType: 'incident' | 'case';

  /**
   * Full Description
   * Specify a long-form description of the issue.
   *
   */
  description?: string;

  /**
   * Severity
   * Specify the severity of the issue described by the trouble ticket. Maps to the Priority field.
   *
   */
  severity?:
    | 'undefined'
    | 'undefined'
    | 'undefined'
    | 'undefined'
    | 'undefined';

  /**
   * Name
   * Specify the name of the ticket, typically a short description of the issue. Maps to the Short description field.
   *
   * **Example**:
   * ```
   * Bug report from Customer A
   * ```
   */
  name?: string;

  /**
   * Short Description
   * Specify a one-line description of the issue.
   *
   */
  shortDescription?: string;

  /**
   * Status
   * Specify the current status of the ticket. Maps to the State field. You may provide the choice label or value.
   *
   * **Example**:
   * ```
   * New
   * ```
   */
  status?: string;

  /**
   * Related party ID
   * Specify the sys_id of the related party associated with this ticket.
   *
   * **Example**:
   * ```
   * 9d385017c611228701d22104cc95c371
   * ```
   */
  relatedPartyId?: string;

  /**
   * Related Party Type
   * Specify the type of the related party associated with this ticket.
   *
   */
  referredType?: 'customer' | 'customer_contact';

  /**
   * Channel Name
   * Specify the name of the contact method.
   *
   * **Example**:
   * ```
   * Virtual Agent
   * ```
   */
  channelName?: string;

  /**
   * Notes
   *
   * **Example**:
   * ```
   *
   *[
   *  {
   *    "text": "Note content",
   *    "@type": "work_notes"
   *  }
   *]
   * ```
   */
  note?: string;
};

