/**
 * Item Created intent type
 */
type SharepointTriggerItemCreated = {
  /**
   * List ID
   * The List ID of the item in Sharepoint. Use Connect Portal User Settings to allow users to select a List.
   *
   * **Example**:
   * ```
   * 22e03ef3-6ef4-424d-a1d3-92a337807c30
   * ```
   */
  listId: string;

  /**
   * Apply field mapping
   *
   * **Example**:
   * ```
   * {{userSettings.MappingInput.objectMapping}}
   * ```
   */
  objectMapping?: string;
};

