/**
 * Create Project intent type
 */
type TodoistCreateProject = {
  /**
   * Name
   * Name of the project.
   *
   * **Example**:
   * ```
   * Akatsuki
   * ```
   */
  name: string;

  /**
   * Parent ID
   * Parent project ID.
   *
   * **Example**:
   * ```
   * 1064843106
   * ```
   */
  parent_id?: string;

  /**
   * Color
   *
   * **Example**:
   * ```
   * charcoal
   * ```
   */
  color?: string;

  /**
   * Is Favorite
   * Whether the project is a favorite.
   *
   */
  is_favorite?: boolean;

  /**
   * Additional Fields
   *
   * **Example**:
   * ```
   * {
   *  color: "red"
   *}
   * ```
   */
  projectAdditionalFields?: string;

  /**
   * Record Type
   *
   */
  recordType?: string;
};

