/**
 * Create Task intent type
 */
type TodoistCreateTask = {
  /**
   * Content
   *
   * **Example**:
   * ```
   * Buy Sword
   * ```
   */
  content: string;

  /**
   * Description
   * A description for the task.
   *
   * **Example**:
   * ```
   * Pirate King
   * ```
   */
  description?: string;

  /**
   * Project ID
   * Task project ID. If not set, task is put to user's Inbox.
   *
   */
  project_id?: string;

  /**
   * Section ID
   * ID of section to put task into.
   *
   */
  section_id?: string;

  /**
   * Parent ID
   * Parent task ID.
   *
   * **Example**:
   * ```
   * 1064843106
   * ```
   */
  parent_id?: string;

  /**
   * Due String
   *
   * **Example**:
   * ```
   * tomorrow at 12:00
   * ```
   */
  due_string?: string;

  /**
   * Due Date
   * Specific date in YYYY-MM-DD format relative to user’s timezone.
   *
   */
  due_date?: string;

  /**
   * Priority
   * Task priority from 1 (normal) to 4 (urgent).
   *
   * **Example**:
   * ```
   * 4
   * ```
   */
  priority?: string;

  /**
   * Additional Fields
   *
   * **Example**:
   * ```
   * {
   *  assignee_id: "a70bdf0f"
   *}
   * ```
   */
  taskAdditionalFields?: string;

  /**
   * Record Type
   *
   */
  recordType?: string;
};

