import { ActionStep, ActionTriggerStep } from '@useparagon/core';
import {
  CustomDropdownField,
  DynamicMappingField,
  UserDefinedComboInputOptions,
} from '@useparagon/core/inputs';
import { IntegrationOptions } from '@useparagon/core/integration';

import { IntentToInputOutput } from './intents';
import { IntentToInputOutput as TriggerIntentToInputOutput } from './triggers';

type ITrelloIntegrationUserDefinedInput = {
  id: string;
  title: string;
  required?: boolean;
  tooltip?: string;
} & (
  | {
      type:
        | 'text'
        | 'number'
        | 'email'
        | 'url'
        | 'boolean'
        | 'password'
        | 'board';
    }
  | {
      type: 'custom_dropdown';
      key: string;
      customDropdownOptions?: CustomDropdownField[];
    }
  | ({
      type: 'list' | 'member';
    } & UserDefinedComboInputOptions)
  | {
      type: 'custom_field_mapping';
      objectName: string;
      // these object types values will be used in preview portal
      mockObjectTypes?: DynamicMappingField[];
      // these integration fields values will be used in preview portal
      mockIntegrationFields?: DynamicMappingField[];
    }
);

/**
 * map b/w input type to result
 */
export type InputResultMap = {
  email: string;
  url: string;
  password: string;
  boolean: boolean;
  text: string;
  number: number;
  custom_dropdown: string;
  custom_field_mapping: {
    object: string;
    fields: Record<string, any>;
  };
  board: string;
  list: {
    board: string;
    list: string;
  };
  member: {
    board: string;
    members: string;
  };
};

/**
 * pass { variableName for input => Input }
 * this should be used whenever we are creating inputs for integrations/workflows
 * as it will infer proper types
 */
export declare const createInputs: <
  T extends Record<string, ITrelloIntegrationUserDefinedInput>,
>(
  inputs: T,
) => T;

/**
 * integration type for trello
 */
export type ITrelloIntegration = {
  /**
   * name of integration
   */
  name: string;

  /**
   * action step methods
   */
  actions: {
    /**
     * Search Cards
     * method for TRELLO_SEARCH_CARDS
     */
    searchCards: (
      inputs: IntentToInputOutput['TRELLO_SEARCH_CARDS'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['TRELLO_SEARCH_CARDS'][0],
      IntentToInputOutput['TRELLO_SEARCH_CARDS'][1]
    >;

    /**
     * Get Cards In Board
     * method for TRELLO_GET_CARDS_IN_BOARD
     */
    getCardsInBoard: (
      inputs: IntentToInputOutput['TRELLO_GET_CARDS_IN_BOARD'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['TRELLO_GET_CARDS_IN_BOARD'][0],
      IntentToInputOutput['TRELLO_GET_CARDS_IN_BOARD'][1]
    >;

    /**
     * Create Card
     * method for TRELLO_CREATE_CARD
     */
    createCard: (
      inputs: IntentToInputOutput['TRELLO_CREATE_CARD'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['TRELLO_CREATE_CARD'][0],
      IntentToInputOutput['TRELLO_CREATE_CARD'][1]
    >;

    /**
     * Update Card
     * method for TRELLO_UPDATE_CARD
     */
    updateCard: (
      inputs: IntentToInputOutput['TRELLO_UPDATE_CARD'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['TRELLO_UPDATE_CARD'][0],
      IntentToInputOutput['TRELLO_UPDATE_CARD'][1]
    >;

    /**
     * Delete Card
     * method for TRELLO_DELETE_CARD
     */
    deleteCard: (
      inputs: IntentToInputOutput['TRELLO_DELETE_CARD'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['TRELLO_DELETE_CARD'][0],
      IntentToInputOutput['TRELLO_DELETE_CARD'][1]
    >;

    /**
     * Get Lists In Board
     * method for TRELLO_GET_LISTS_IN_BOARD
     */
    getListsInBoard: (
      inputs: IntentToInputOutput['TRELLO_GET_LISTS_IN_BOARD'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['TRELLO_GET_LISTS_IN_BOARD'][0],
      IntentToInputOutput['TRELLO_GET_LISTS_IN_BOARD'][1]
    >;

    /**
     * Search Boards
     * method for TRELLO_SEARCH_BOARDS
     */
    searchBoards: (
      inputs: IntentToInputOutput['TRELLO_SEARCH_BOARDS'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['TRELLO_SEARCH_BOARDS'][0],
      IntentToInputOutput['TRELLO_SEARCH_BOARDS'][1]
    >;
  };

  /**
   * trigger step methods
   */
  triggers: {
    /**
     * Board Created
     * method for TRELLO_TRIGGER_BOARD_CREATED
     */
    boardCreated: (
      inputs: TriggerIntentToInputOutput['TRELLO_TRIGGER_BOARD_CREATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['TRELLO_TRIGGER_BOARD_CREATED'][0],
      TriggerIntentToInputOutput['TRELLO_TRIGGER_BOARD_CREATED'][1]
    >;

    /**
     * Board Updated
     * method for TRELLO_TRIGGER_BOARD_UPDATED
     */
    boardUpdated: (
      inputs: TriggerIntentToInputOutput['TRELLO_TRIGGER_BOARD_UPDATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['TRELLO_TRIGGER_BOARD_UPDATED'][0],
      TriggerIntentToInputOutput['TRELLO_TRIGGER_BOARD_UPDATED'][1]
    >;

    /**
     * Card Created
     * method for TRELLO_TRIGGER_CARD_CREATED
     */
    cardCreated: (
      inputs: TriggerIntentToInputOutput['TRELLO_TRIGGER_CARD_CREATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['TRELLO_TRIGGER_CARD_CREATED'][0],
      TriggerIntentToInputOutput['TRELLO_TRIGGER_CARD_CREATED'][1]
    >;

    /**
     * Card Updated
     * method for TRELLO_TRIGGER_CARD_UPDATED
     */
    cardUpdated: (
      inputs: TriggerIntentToInputOutput['TRELLO_TRIGGER_CARD_UPDATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['TRELLO_TRIGGER_CARD_UPDATED'][0],
      TriggerIntentToInputOutput['TRELLO_TRIGGER_CARD_UPDATED'][1]
    >;

    /**
     * Comment Created
     * method for TRELLO_TRIGGER_COMMENT_CREATED
     */
    commentCreated: (
      inputs: TriggerIntentToInputOutput['TRELLO_TRIGGER_COMMENT_CREATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['TRELLO_TRIGGER_COMMENT_CREATED'][0],
      TriggerIntentToInputOutput['TRELLO_TRIGGER_COMMENT_CREATED'][1]
    >;
  };
};
