/**
 * Search Cards intent type
 */
type TrelloSearchCards = {
  /**
   * Search Query
   * Search for cards by a phrase in their title, description, or other properties.
   *
   */
  query: string;
};

/**
 * Get Cards In Board intent type
 */
type TrelloGetCardsInBoard = {
  /**
   * Board
   * Select a Board to get cards from. Use Connect Portal Workflow Settings to allow users to select a Trello Board.
   *
   */
  boardId: string;
};

/**
 * Create Card intent type
 */
type TrelloCreateCard = {
  /**
   * List
   * Select a List that this card should appear in. Use Connect Portal Workflow Settings to allow users to select a Trello List.
   *
   * **Example**:
   * ```
   * {{settings.list}}
   * ```
   */
  listId: string;

  /**
   * Name
   *
   */
  name?: string;

  /**
   * Description
   *
   */
  description?: string;

  /**
   * Position
   * Set the position of the new card to top, bottom, or a positive number.
   *
   */
  position?: string;

  /**
   * Due Date
   * Specify a date for this card to be due.
   *
   */
  dueDate?: string;

  /**
   * Due Date Complete
   * Specify whether or not the card’s due date should be marked complete.
   *
   */
  isDueDateComplete?: 'no' | 'yes';

  /**
   * Member IDs
   * Specify an array of member IDs to add to the card.
   *
   */
  memberIds?: string;

  /**
   * Label IDs
   * Specify an array of label IDs to add to the card.
   *
   */
  labelIds?: string;

  /**
   * Address
   * For use with Trello’s Map View.
   *
   */
  address?: string;

  /**
   * Location Name
   * For use with Trello’s Map View.
   *
   */
  locationName?: string;

  /**
   * Coordinates
   * For use with Trello’s Map View. Use a lat,long-formatted string.
   *
   */
  coordinates?: string;
};

/**
 * Update Card intent type
 */
type TrelloUpdateCard = {
  /**
   * Card ID
   * The ID of the card to update.
   *
   */
  cardId: string;

  /**
   * Board
   * Select a Board to move this card into. Use Connect Portal Workflow Settings to allow users to select a Trello Board. If this field is specified, a List must also be specified below.
   *
   * **Example**:
   * ```
   * {{settings.board}}
   * ```
   */
  boardId?: string;

  /**
   * List
   * Select a List that this card should appear in. Use Connect Portal Workflow Settings to allow users to select a Trello List.
   *
   * **Example**:
   * ```
   * {{settings.list}}
   * ```
   */
  listId?: string;

  /**
   * Name
   *
   */
  name?: string;

  /**
   * Description
   *
   */
  description?: string;

  /**
   * Closed
   * Specify whether or not the card should be archived.
   *
   */
  isClosed?: 'no' | 'yes';

  /**
   * Position
   * Set the position of the new card to top, bottom, or a positive number.
   *
   */
  position?: string;

  /**
   * Due Date
   * Specify a date for this card to be due.
   *
   */
  dueDate?: string;

  /**
   * Due Date Complete
   * Specify whether or not the card’s due date should be marked complete.
   *
   */
  isDueDateComplete?: 'no' | 'yes';

  /**
   * Member IDs
   * Specify an array of member IDs to add to the card.
   *
   */
  memberIds?: string;

  /**
   * Label IDs
   * Specify an array of label IDs to add to the card.
   *
   */
  labelIds?: string;

  /**
   * Address
   * For use with Trello’s Map View.
   *
   */
  address?: string;

  /**
   * Location Name
   * For use with Trello’s Map View.
   *
   */
  locationName?: string;

  /**
   * Coordinates
   * For use with Trello’s Map View. Use a lat,long-formatted string.
   *
   */
  coordinates?: string;
};

/**
 * Delete Card intent type
 */
type TrelloDeleteCard = {
  /**
   * Card ID
   * The ID of the card to delete.
   *
   */
  cardId: string;
};

/**
 * Get Lists In Board intent type
 */
type TrelloGetListsInBoard = {
  /**
   * Board
   * Select a Board to get lists from. Use Connect Portal Workflow Settings to allow users to select a Trello Board.
   *
   */
  boardId: string;
};

/**
 * Search Boards intent type
 */
type TrelloSearchBoards = {
  /**
   * Search Query
   * Search for boards by a phrase in their title or other properties.
   *
   */
  query: string;
};

export type IntentToInputOutput = {
  /**
   * TRELLO_SEARCH_CARDS
   */
  TRELLO_SEARCH_CARDS: [TrelloSearchCards, any];

  /**
   * TRELLO_GET_CARDS_IN_BOARD
   */
  TRELLO_GET_CARDS_IN_BOARD: [TrelloGetCardsInBoard, any];

  /**
   * TRELLO_CREATE_CARD
   */
  TRELLO_CREATE_CARD: [TrelloCreateCard, any];

  /**
   * TRELLO_UPDATE_CARD
   */
  TRELLO_UPDATE_CARD: [TrelloUpdateCard, any];

  /**
   * TRELLO_DELETE_CARD
   */
  TRELLO_DELETE_CARD: [TrelloDeleteCard, any];

  /**
   * TRELLO_GET_LISTS_IN_BOARD
   */
  TRELLO_GET_LISTS_IN_BOARD: [TrelloGetListsInBoard, any];

  /**
   * TRELLO_SEARCH_BOARDS
   */
  TRELLO_SEARCH_BOARDS: [TrelloSearchBoards, any];
};
