import { ActionStep, ActionTriggerStep } from '@useparagon/core';
import {
  CustomDropdownField,
  UserDefinedFieldMappingOptions,
} from '@useparagon/core/inputs';
import { IntegrationOptions } from '@useparagon/core/integration';

import { IntentToInputOutput } from './intents';
import { IntentToInputOutput as TriggerIntentToInputOutput } from './triggers';

type IXeroIntegrationUserDefinedInput = {
  id: string;
  title: string;
  required?: boolean;
  tooltip?: string;
} & (
  | {
      type:
        | 'text'
        | 'number'
        | 'email'
        | 'url'
        | 'boolean'
        | 'password'
        | 'account';
    }
  | {
      type: 'custom_dropdown';
      key: string;
      customDropdownOptions?: CustomDropdownField[];
    }
  | ({
      type: 'field_mapping';
    } & UserDefinedFieldMappingOptions)
);

/**
 * map b/w input type to result
 */
export type InputResultMap = {
  email: string;
  url: string;
  password: string;
  boolean: boolean;
  text: string;
  number: number;
  custom_dropdown: string;

  account: string;
  field_mapping: {
    record_type: string;
    fields: Record<string, any>;
  };
};

/**
 * pass { variableName for input => Input }
 * this should be used whenever we are creating inputs for integrations/workflows
 * as it will infer proper types
 */
export declare const createInputs: <
  T extends Record<string, IXeroIntegrationUserDefinedInput>,
>(
  inputs: T,
) => T;

/**
 * integration type for xero
 */
export type IXeroIntegration = {
  /**
   * name of integration
   */
  name: string;

  /**
   * action step methods
   */
  actions: {
    /**
     * Get Accounts
     * method for XERO_GET_ACCOUNTS
     */
    getAccounts: (
      inputs: IntentToInputOutput['XERO_GET_ACCOUNTS'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['XERO_GET_ACCOUNTS'][0],
      IntentToInputOutput['XERO_GET_ACCOUNTS'][1]
    >;

    /**
     * Get Contacts
     * method for XERO_GET_CONTACTS
     */
    getContacts: (
      inputs: IntentToInputOutput['XERO_GET_CONTACTS'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['XERO_GET_CONTACTS'][0],
      IntentToInputOutput['XERO_GET_CONTACTS'][1]
    >;

    /**
     * Create Customer
     * method for XERO_CREATE_CUSTOMER
     */
    createCustomer: (
      inputs: IntentToInputOutput['XERO_CREATE_CUSTOMER'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['XERO_CREATE_CUSTOMER'][0],
      IntentToInputOutput['XERO_CREATE_CUSTOMER'][1]
    >;

    /**
     * Update Customer
     * method for XERO_UPDATE_CUSTOMER
     */
    updateCustomer: (
      inputs: IntentToInputOutput['XERO_UPDATE_CUSTOMER'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['XERO_UPDATE_CUSTOMER'][0],
      IntentToInputOutput['XERO_UPDATE_CUSTOMER'][1]
    >;

    /**
     * Get invoices
     * method for XERO_GET_INVOICES
     */
    getInvoices: (
      inputs: IntentToInputOutput['XERO_GET_INVOICES'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['XERO_GET_INVOICES'][0],
      IntentToInputOutput['XERO_GET_INVOICES'][1]
    >;

    /**
     * Create invoice
     * method for XERO_CREATE_INVOICE
     */
    createInvoice: (
      inputs: IntentToInputOutput['XERO_CREATE_INVOICE'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['XERO_CREATE_INVOICE'][0],
      IntentToInputOutput['XERO_CREATE_INVOICE'][1]
    >;

    /**
     * Update invoice
     * method for XERO_UPDATE_INVOICE
     */
    updateInvoice: (
      inputs: IntentToInputOutput['XERO_UPDATE_INVOICE'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['XERO_UPDATE_INVOICE'][0],
      IntentToInputOutput['XERO_UPDATE_INVOICE'][1]
    >;

    /**
     * Send invoice
     * method for XERO_SEND_INVOICE
     */
    sendInvoice: (
      inputs: IntentToInputOutput['XERO_SEND_INVOICE'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['XERO_SEND_INVOICE'][0],
      IntentToInputOutput['XERO_SEND_INVOICE'][1]
    >;

    /**
     * Get payments
     * method for XERO_GET_PAYMENTS
     */
    getPayments: (
      inputs: IntentToInputOutput['XERO_GET_PAYMENTS'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['XERO_GET_PAYMENTS'][0],
      IntentToInputOutput['XERO_GET_PAYMENTS'][1]
    >;

    /**
     * Create payments
     * method for XERO_CREATE_PAYMENT
     */
    createPayment: (
      inputs: IntentToInputOutput['XERO_CREATE_PAYMENT'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['XERO_CREATE_PAYMENT'][0],
      IntentToInputOutput['XERO_CREATE_PAYMENT'][1]
    >;
  };

  /**
   * trigger step methods
   */
  triggers: {
    /**
     * New Account
     * method for XERO_TRIGGER_ACCOUNT_CREATED
     */
    accountCreated: (
      inputs: TriggerIntentToInputOutput['XERO_TRIGGER_ACCOUNT_CREATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['XERO_TRIGGER_ACCOUNT_CREATED'][0],
      TriggerIntentToInputOutput['XERO_TRIGGER_ACCOUNT_CREATED'][1]
    >;

    /**
     * New Customer
     * method for XERO_TRIGGER_CUSTOMER_CREATED
     */
    customerCreated: (
      inputs: TriggerIntentToInputOutput['XERO_TRIGGER_CUSTOMER_CREATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['XERO_TRIGGER_CUSTOMER_CREATED'][0],
      TriggerIntentToInputOutput['XERO_TRIGGER_CUSTOMER_CREATED'][1]
    >;

    /**
     * New Invoice
     * method for XERO_TRIGGER_INVOICE_CREATED
     */
    invoiceCreated: (
      inputs: TriggerIntentToInputOutput['XERO_TRIGGER_INVOICE_CREATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['XERO_TRIGGER_INVOICE_CREATED'][0],
      TriggerIntentToInputOutput['XERO_TRIGGER_INVOICE_CREATED'][1]
    >;
  };
};
