import { ActionStep, ActionTriggerStep } from '@useparagon/core';
import {
  CustomDropdownField,
  UserDefinedFieldMappingOptions,
} from '@useparagon/core/inputs';
import { IntegrationOptions } from '@useparagon/core/integration';

import { IntentToInputOutput } from './intents';
import { IntentToInputOutput as TriggerIntentToInputOutput } from './triggers';

type IZohocrmIntegrationUserDefinedInput = {
  id: string;
  title: string;
  required?: boolean;
  tooltip?: string;
} & (
  | {
      type:
        | 'text'
        | 'number'
        | 'email'
        | 'url'
        | 'boolean'
        | 'password'
        | 'record_type';
    }
  | {
      type: 'custom_dropdown';
      key: string;
      customDropdownOptions?: CustomDropdownField[];
    }
  | ({
      type: 'field_mapping';
    } & UserDefinedFieldMappingOptions)
);

/**
 * map b/w input type to result
 */
export type InputResultMap = {
  email: string;
  url: string;
  password: string;
  boolean: boolean;
  text: string;
  number: number;
  custom_dropdown: string;

  record_type: string;
  field_mapping: {
    record_type: string;
    field: Record<string, any>;
  };
};

/**
 * pass { variableName for input => Input }
 * this should be used whenever we are creating inputs for integrations/workflows
 * as it will infer proper types
 */
export declare const createInputs: <
  T extends Record<string, IZohocrmIntegrationUserDefinedInput>,
>(
  inputs: T,
) => T;

/**
 * integration type for zohocrm
 */
export type IZohocrmIntegration = {
  /**
   * name of integration
   */
  name: string;

  /**
   * action step methods
   */
  actions: {
    /**
     * Create record
     * method for ZOHO_CRM_CREATE_RECORD
     */
    zohoCrmCreateRecord: (
      inputs: IntentToInputOutput['ZOHO_CRM_CREATE_RECORD'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['ZOHO_CRM_CREATE_RECORD'][0],
      IntentToInputOutput['ZOHO_CRM_CREATE_RECORD'][1]
    >;

    /**
     * Update record
     * method for ZOHO_CRM_UPDATE_RECORD
     */
    zohoCrmUpdateRecord: (
      inputs: IntentToInputOutput['ZOHO_CRM_UPDATE_RECORD'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['ZOHO_CRM_UPDATE_RECORD'][0],
      IntentToInputOutput['ZOHO_CRM_UPDATE_RECORD'][1]
    >;

    /**
     * Get record by ID
     * method for ZOHO_CRM_GET_RECORD_BY_ID
     */
    zohoCrmGetRecordById: (
      inputs: IntentToInputOutput['ZOHO_CRM_GET_RECORD_BY_ID'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['ZOHO_CRM_GET_RECORD_BY_ID'][0],
      IntentToInputOutput['ZOHO_CRM_GET_RECORD_BY_ID'][1]
    >;

    /**
     * Search Records
     * method for ZOHO_CRM_SEARCH_RECORDS
     */
    zohoCrmSearchRecords: (
      inputs: IntentToInputOutput['ZOHO_CRM_SEARCH_RECORDS'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['ZOHO_CRM_SEARCH_RECORDS'][0],
      IntentToInputOutput['ZOHO_CRM_SEARCH_RECORDS'][1]
    >;

    /**
     * Search records by COQL query
     * method for ZOHO_CRM_WRITE_COQL_QUERY
     */
    zohoCrmWriteCoqlQuery: (
      inputs: IntentToInputOutput['ZOHO_CRM_WRITE_COQL_QUERY'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['ZOHO_CRM_WRITE_COQL_QUERY'][0],
      IntentToInputOutput['ZOHO_CRM_WRITE_COQL_QUERY'][1]
    >;

    /**
     * Delete Record
     * method for ZOHO_CRM_DELETE_RECORD
     */
    zohoCrmDeleteRecord: (
      inputs: IntentToInputOutput['ZOHO_CRM_DELETE_RECORD'][0],
      options: IntegrationOptions,
    ) => ActionStep<
      IntentToInputOutput['ZOHO_CRM_DELETE_RECORD'][0],
      IntentToInputOutput['ZOHO_CRM_DELETE_RECORD'][1]
    >;
  };

  /**
   * trigger step methods
   */
  triggers: {
    /**
     * New Record
     * method for ZOHO_CRM_TRIGGER_RECORD_CREATED
     */
    zohoCrmTriggerRecordCreated: (
      inputs: TriggerIntentToInputOutput['ZOHO_CRM_TRIGGER_RECORD_CREATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['ZOHO_CRM_TRIGGER_RECORD_CREATED'][0],
      TriggerIntentToInputOutput['ZOHO_CRM_TRIGGER_RECORD_CREATED'][1]
    >;

    /**
     * Record Updated
     * method for ZOHO_CRM_TRIGGER_RECORD_UPDATED
     */
    zohoCrmTriggerRecordUpdated: (
      inputs: TriggerIntentToInputOutput['ZOHO_CRM_TRIGGER_RECORD_UPDATED'][0],
    ) => ActionTriggerStep<
      TriggerIntentToInputOutput['ZOHO_CRM_TRIGGER_RECORD_UPDATED'][0],
      TriggerIntentToInputOutput['ZOHO_CRM_TRIGGER_RECORD_UPDATED'][1]
    >;
  };
};
