import { ConditionalInput } from '@useparagon/core/steps/library/conditional';

/**
 * Create record intent type
 */
type ZohoCrmCreateRecord = {} & (
  | {
      /**
       * Record type
       *
       */
      recordType: 'Accounts';

      /**
       * Accout Name
       * Specify the Account Name.
       *
       */
      Account_Name: string;

      /**
       * Website
       *
       */
      Website?: string;

      /**
       * Phone
       *
       */
      Phone?: string;

      /**
       * Description
       *
       */
      Description?: string;

      /**
       * Owner ID
       *
       */
      Owner?: string;

      /**
       * Additional Fields
       * Specify any other fields that should be updated in JSON below. Use Connect Portal Workflow Settings to allow users to select which Account fields to update.
       *
       * **Example**:
       * ```
       * {
       *  "Annual_Revenue": 850000,
       *  "Shipping_Street": null,
       *  "Ownership": "Partnership",
       *}
       * ```
       */
      accountAdditionalFields?: string;
    }
  | {
      /**
       * Record type
       *
       */
      recordType: 'Contacts';

      /**
       * First Name
       *
       */
      First_Name?: string;

      /**
       * Last Name
       *
       */
      Last_Name: string;

      /**
       * Accout Name
       * Specify the Account Name.
       *
       */
      Account_Name?: string;

      /**
       * Email
       *
       */
      Email?: string;

      /**
       * Title
       * Title of the contact, such as CEO or Vice President.
       *
       */
      Title?: string;

      /**
       * Description
       *
       */
      Description?: string;

      /**
       * Additional Fields
       * Specify any other fields that should be updated in JSON below. Use Connect Portal Workflow Settings to allow users to select which Contact fields to update.
       *
       * **Example**:
       * ```
       * {
       *  "Twitter": "krismarrier_sample",
       *  "Other_Zip": null,
       *  "Mailing_Street": "228 Runamuck Pl #2808",
       *}
       * ```
       */
      contactAdditionalFields?: string;
    }
  | {
      /**
       * Record type
       *
       */
      recordType: 'Leads';

      /**
       * First Name
       *
       */
      First_Name?: string;

      /**
       * Last Name
       *
       */
      Last_Name: string;

      /**
       * Company
       *
       */
      Company?: string;

      /**
       * Email
       *
       */
      Email?: string;

      /**
       * Phone
       *
       */
      Phone?: string;

      /**
       * Website
       *
       */
      Website?: string;

      /**
       * Title
       * Title of the contact, such as CEO or Vice President.
       *
       */
      Designation?: string;

      /**
       * Lead Status
       *
       */
      Lead_Status?: string;

      /**
       * Description
       *
       */
      Description?: string;

      /**
       * Additional Fields
       * Specify any other fields that should be updated in JSON below. Use Connect Portal Workflow Settings to allow users to select which Lead fields to update.
       *
       * **Example**:
       * ```
       * {
       *  "State": C.A,
       *  "Skype_ID": skype_12id,
       *}
       * ```
       */
      leadAdditionalFields?: string;
    }
  | {
      /**
       * Record type
       *
       */
      recordType: 'Tasks';

      /**
       * Contact ID
       * The ID of the Contact this Task is related to. Specify this when you want to associate a Task with a Contact.
       *
       */
      Who_Id?: string;

      /**
       * Related to Record ID
       * The ID of the related record the Task is associated with. Specify this when you want to associate a Task with an Account, Lead or other module record.
       *
       */
      What_Id?: string;

      /**
       * Associated Module Name
       * The Zoho CRM API name of the parent module the Task is associated with. This key is mandatory when including a Contact ID or Related to Record ID.
       *
       * **Example**:
       * ```
       * Accounts
       * ```
       */
      $se_module?: string;

      /**
       * Subject
       *
       */
      Subject: string;

      /**
       * Description
       *
       */
      Description?: string;

      /**
       * Status
       *
       */
      Status?: 'Not Started' | 'In Progress' | 'Completed';

      /**
       * Additional Fields
       * Specify any other fields that should be updated in JSON below. Use Connect Portal Workflow Settings to allow users to select which Task fields to update.
       *
       * **Example**:
       * ```
       * {
       *  "Due_Date": "2022-03-18",
       *  "Priority": "Highest",
       *}
       * ```
       */
      taskAdditionalFields?: string;
    }
  | {
      /**
       * Record type
       *
       */
      recordType: '<specified by objectName property>';

      /**
       * Object Name
       * The API Name of the Object to create. Use Connect Portal Workflow Settings to allow users to select a Field Mapping.
       *
       * **Example**:
       * ```
       * {{settings.objectType}}
       * ```
       */
      objectName: string;

      /**
       * Record Fields
       * Use Connect Portal Workflow Settings to allow users to select a Field Mapping to specify which fields to update.
       *
       * **Example**:
       * ```
       * {
       *  "Annual_Revenue": 850000,
       *  "Shipping_Street": null,
       *  "Ownership": "Partnership",
       *}
       * ```
       */
      additionalFields?: string;
    }
);

