/**
 * Get record by ID intent type
 */
type ZohoCrmGetRecordById = {
  /**
   * Record ID
   * Specify the record ID to fetch.
   *
   */
  recordId: string;
} & (
  | {
      /**
       * Record type
       *
       */
      recordType: '<specified by objectName property>';

      /**
       * Object Name
       * The API Name of the Object to get record. Use Connect Portal Workflow Settings to allow users to select a Field Mapping.
       *
       * **Example**:
       * ```
       * {{settings.objectType}}
       * ```
       */
      objectName: string;
    }
  | {
      /**
       * Record type
       *
       */
      recordType: 'Accounts' | 'Contacts' | 'Leads' | 'Tasks';
    }
);

