/**
 * Search records by COQL query intent type
 */
type ZohoCrmWriteCoqlQuery = {
  /**
   * COQL Query
   *
   * **Example**:
   * ```
   * SELECT Account_Name, Description
   *FROM Accounts
   *WHERE Name = 'Ethan'
   * ```
   */
  query: string;
};

